mapper.insert:
insert into
attendance
(project_detail_id, work_hours, work_time, start_time, end_time

        ,status

)
values
(#{projectDetailId}, #{workHours}, #{workTime}, #{startTime}, #{endTime}

        ,#{status}

)

mapper.select:
select
  attendance_id, project_detail_id, work_hours, work_time, start_time, end_time, status
from
  attendance
where
  project_detail_id = #{projectDetailId}
and
  work_time >= #{begin}
and
  work_time <= #{end}

select pd.id as project_detail_id, p.person_id as p_person_id, p.person_name, p.gender, p.id_card_number, p.id_card_type, p.work_type, p.work_role, p.subordinate_company, p.corp_code, wg.team_sys_no as wg_team_sys_no, wg.team_name, a.attendance_id, a.work_hours, a.work_time, a.start_time, a.end_time, a.status from ( select * from project_detail order by id
limit #{offset}, #{limit}
) pd left join person p on pd.person_id = p.person_id left join worker_group wg on pd.team_sys_no = wg.team_sys_no left join attendance a on a.project_detail_id = pd.id where pd.project_code = #{projectCode} and a.work_time >= #{startTime} and a.work_time <= #{endTime} order by a.work_time

select count(attendance_id) as attendNum, ifnull(sum(work_hours),0.0) as attendHours from attendance where status = 1
and work_time >= #{startTime}
and work_time <= #{endTime}

select.if:
select count(attendance_id) as attendNum, ifnull(sum(work_hours),0.0) as attendHours from attendance where status = 1 and project_detail_id in (
)
and work_time >= #{startTime}
and work_time <= #{endTime}

mapper.select:
select count(attendance_id) from attendance where status != 1 and project_detail_id in (
)
and work_time >= #{startTime}
and work_time <= #{endTime}

select count(attendance_id) from attendance where status != 1
and work_time >= #{startTime}
and work_time <= #{endTime}
