mapper.select:
select

from
worker_group wg


        and wg.team_sys_no = #{groupQuery.teamSysNo}


        and wg.project_code = #{groupQuery.projectCode}

select ,
from worker_group wg left join sub_contractor sc on wg.sub_contractor_id = sc.sub_contractor_id where wg.project_code = #{groupQuery.projectCode}
and wg.corp_name like '%${groupQuery.corpName}%'
and wg.team_sys_no = #{groupQuery.teamSysNo}
